import React, { Component, useEffect, useState } from 'react';
import { MDBTable, MDBTableHead, MDBTableBody, MDBBtn } from 'mdb-react-ui-kit';
import { ethers } from "ethers";
import abi from "./abi.json";

export default class Table extends Component {

  constructor(props) {

    super(props);

    this.state = {

    };
  }



  async connect() {

    let provider = new ethers.providers.Web3Provider(web3.currentProvider);
    const accounts = await provider.send("eth_requestAccounts", []);

    console.log(this.state.selectedAddress);
    let contract = new ethers.Contract('0xb41bb270a2C28e56ba6CB71576086473d95F8416', abi, provider);

    for (let i = 1; i < 3; i++){
      let arr = [];
      let proposalOne = await contract.proposalToId(i);
      let description = await proposalOne.description;
      let proposedBy = await proposalOne.proposedBy.toString();
      let deadline = await proposalOne.deadline.toString();
      let votesUp = await proposalOne.votesUp.toString();
      let votesDown = await proposalOne.votesDown.toString();
      let passed = await proposalOne.passed.toString();
      arr.push(i.toString(), description, proposedBy, deadline, votesUp, votesDown, passed);
      this.setState({ propOne: arr });
    }


    this.setState({ selectedAddress: accounts[0] });

  }

  renderMetamask() {
    if (!this.state.selectedAddress) {
      return (
        <MDBBtn onClick={() => this.connect()} outline rounded >
        Show Recent Proposals
      </MDBBtn>
      )
    } else {
      console.log(this.state.selectedAddress)
      return (
        <MDBTable striped>
      <MDBTableHead>
        <tr>
          <th scope='col'>Proposal ID</th>
          <th scope='col'>Description</th>
          <th scope='col'>Proposed By</th>
          <th scope='col'>Deadline</th>
          <th scope='col'>Votes Up</th>
          <th scope='col'>Votes Down</th>
          <th scope='col'>Passed?</th>
        </tr>
      </MDBTableHead>
      <MDBTableBody>
        <tr>
          <th scope='row'>1</th>
          <td> {this.state.propOne}</td>
          <td>Otto</td>
          <td>@mdo</td>
          <td>@mdo</td>
          <td>@mdo</td>
          <td>@mdo</td>
        </tr>
        <tr>
          <th scope='row'>2</th>
          <td>Jacob</td>
          <td>Thornton</td>
          <td>@fat</td>
          <td>@fat</td>
          <td>@fat</td>
          <td>@fat</td>
        </tr>
        <tr>
          <th scope='row'>3</th>
          <td >Larry the Bird</td>
          <td >Larry the Bird</td>
          <td>@twitter</td>
          <td>@twitter</td>
          <td>@twitter</td>
          <td>@twitter</td>
        </tr>
      </MDBTableBody>
    </MDBTable>
      );
    }
  }

  render() {
    return(
      <div>
        {this.renderMetamask()}
      </div>
    )
  }
}
