import React, { Component, useEffect } from 'react';
import { MDBTable, MDBTableHead, MDBTableBody, MDBBtn } from 'mdb-react-ui-kit';
import { ethers } from "ethers";
import abi from "./abi.json";

export default class Table extends React.Component {

  constructor(props) {

    super(props);

    this.state = {
    };
  }

  async connect() {
console.log('hello');
const provider = new ethers.providers.Web3Provider(window.etherem);
const accounts = await provider.send("eth_requestAccounts", []);
this.setState({ selectedAddress: accounts[0] })
console.log(this.state.selectedAddress);


  }

  render(){
  return (
    <div>
      <MDBBtn onClick={() => this.connect()} outline rounded >
        Show Recent Proposals
      </MDBBtn>
    <MDBTable striped>
      <MDBTableHead>
        <tr>
          <th scope='col'>Proposal ID</th>
          <th scope='col'>Description</th>
          <th scope='col'>Proposed By</th>
          <th scope='col'>Deadline</th>
          <th scope='col'>Votes Up</th>
          <th scope='col'>Votes Down</th>
          <th scope='col'>Passed?</th>
        </tr>
      </MDBTableHead>
      <MDBTableBody>
        <tr>
          <th scope='row'>1</th>
          <td>Mark</td>
          <td>Otto</td>
          <td>@mdo</td>
          <td>@mdo</td>
          <td>@mdo</td>
          <td>@mdo</td>
        </tr>
        <tr>
          <th scope='row'>2</th>
          <td>Jacob</td>
          <td>Thornton</td>
          <td>@fat</td>
          <td>@fat</td>
          <td>@fat</td>
          <td>@fat</td>
        </tr>
        <tr>
          <th scope='row'>3</th>
          <td >Larry the Bird</td>
          <td >Larry the Bird</td>
          <td>@twitter</td>
          <td>@twitter</td>
          <td>@twitter</td>
          <td>@twitter</td>
        </tr>
      </MDBTableBody>
    </MDBTable>
    </div>
  );
}
}
