import React, { Component, useEffect, useState } from 'react';
import {
  MDBRow,
  MDBCol,
  MDBInput,
  MDBCheckbox,
  MDBBtn,
  MDBTextArea
} from 'mdb-react-ui-kit';
import { ethers } from "ethers";
import abi from "./abi.json";
import DateTimePicker from 'react-datetime-picker';
import Navbar from './Navbar';





export default function Form() {

  const [propName, setPropName] = useState('');
  const [propDescription, setPropDescription] = useState('');
  const [propDate, setPropDate] = useState('');

  const submit = event => {
    console.log('hello');
    event.preventDefault();
  }



  const [value, onChange] = useState(new Date());
  return (
    <>
    <Navbar
      title="Submit a Proposal"
      message="If you hold a governance token you can submit a proposal here" />
    <form onSubmit={submit}>
      <MDBInput wrapperClass='mb-4' id='propName' label='Proposal Name' onChange={event => setPropName(event.target.value)}
          value={propName} />
      <MDBRow className='mb-4'>
        <MDBCol>
          <MDBTextArea label='Description' id='propDescription' rows={4} />
        </MDBCol>

      </MDBRow>
      <div>
      <p>Deadline (must be at least 3 days):</p>
      <DateTimePicker onChange={onChange} value={value} id='dateSubmit' />
      </div>
      <br></br>


      <MDBBtn className='mb-4' type='submit' id='globalSubmit' block>
        Submit Proposal
      </MDBBtn>
    </form>
    </>
  );
}
